mapper.select:
select * from chat_room 
where cr_no = #{cr_no}
and cr_pw = #{cr_pw}

select * from chat_mem
where cr_no = #{cr_no}
and mem_id = #{mem_id}

mapper.update:
update chat_mem
set 
	online_check = 'Y'
where cr_no = #{cr_no}
and mem_id = #{mem_id}

mapper.select:
select * from chat_mem
where mem_id != #{mem_id}
and cr_no = #{cr_no}

mapper.update:
update chat_mem
set 
	online_check = 'N'
where cr_no = #{cr_no}
and mem_id = #{mem_id}
